import React, { Component } from "react";
import { Link } from "react-router-dom";

class SignUpForm extends Component {
  state = {
    name: "",
    email: "",
    phone: "",
    password: ""
  };

  onNameChange = e => {
    this.setState({ name: e.target.value });
  };

  onEmailChange = e => {
    this.setState({ email: e.target.value });
  };

  onPhoneChange = e => {
    this.setState({ phone: e.target.value });
  };

  onPassTyped = e => {
    this.setState({ password: e.target.value });
    //console.log(this.state);
  };

  alreadyUser = prevUsers => {
    const currentUser = this.state;
    const userAlready = prevUsers.map(user => user.email === currentUser.email);
    return userAlready ? true : false;
  };

  submitDetails = e => {
    e.preventDefault();

    let prevUsers = [];
    prevUsers.push(JSON.parse(localStorage.getItem("signedInUsers")));
    console.log(prevUsers ? true : false);

    if (prevUsers) {
      let updatingUsers = [];
      console.log(prevUsers);
      if (this.alreadyUser(prevUsers)) {
        alert(
          "The user with this credentials is been registered already. Try loggin in..."
        );
      } else {
        updatingUsers.push(prevUsers);
        updatingUsers.push(this.state);
        localStorage.setItem("signedInUsers", JSON.stringify(updatingUsers));
      }
    } else {
      localStorage.setItem("signedInUsers", JSON.stringify(this.state));
    }

    this.setState({
      name: "",
      email: "",
      phone: "",
      password: ""
    });
  };

  render() {
    return (
      <div className="form">
        <div className="container">
          <form>
            <div className="form-group">
              <label>Name</label>
              <input
                type="text"
                value={this.state.name}
                className="form-control"
                onChange={this.onNameChange}
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={this.state.email}
                className="form-control"
                onChange={this.onEmailChange}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="tel"
                value={this.state.phone}
                className="form-control"
                onChange={this.onPhoneChange}
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={this.state.password}
                className="form-control"
                onChange={this.onPassTyped}
              />
            </div>
            <button
              type="submit"
              className="btn btn-primary btn-block"
              onClick={this.submitDetails}
            >
              Sign Up
            </button>
          </form>
          <br />
          <center>
            <Link to="/login">Already have an account?, Log In!</Link>
          </center>
        </div>
      </div>
    );
  }
}

export default SignUpForm;
