import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";

class AddExpense extends Component {
  state = {
    current_data: this.props.current_data
  };

  addExpense = () => {
    let expensesSize = this.state.current_data.expenses.length;
    let expenseName = this.refs.description;
    let totalAmount = this.refs.amount;
    let amountPerHead =
      Number(totalAmount) / Number(this.state.current_data.members_count);
    let updateCurrentData = this.state.current_data;
    updateCurrentData.expenses["expense" + expensesSize] = expenseName;
    updateCurrentData.friends_name.map((friend, index) => {
      friend["owes_" + index] = amountPerHead;
    });
  };

  render() {
    return (
      <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add expense
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <h6>
              With{" "}
              <b>
                <i>you</i>
              </b>{" "}
              and all of:{" "}
              <b>
                <i>{this.props.group_name}</i>
              </b>
            </h6>
          </div>
          <div>
            <center>
              <span style={{ marginRight: "20px" }}>
                <i className="fa fa-arrow-down"></i>
              </span>
              <label>
                <i>Enter a description</i>
              </label>
            </center>
            <center>
              <input type="text" placeholder="Description" ref="description" />
            </center>
            <center>
              <input type="text" placeholder="Amount" ref="amount" />
            </center>
            <center>
              <label>
                <i>Enter amount</i>
              </label>{" "}
              <span style={{ marginLeft: "60px" }}>
                <i className="fa fa-arrow-up"></i>
              </span>
            </center>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={this.addExpense}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

export default AddExpense;

