import React, { Component } from "react";
import TeamMemberList from "./team_member_list";

class AddGroup extends Component {
  state = {
    members_count: ""
  };

  setCount = count => {
    this.setState({ members_count: count });
  };

  render() {
    return (
      <div>
        <center>
          <h2>
            <label>My Group shall be called as...</label>
          </h2>
          <input
            type="text"
            name="group_name"
            id="group_name"
            placeholder="Group Name"
            style={{ width: "50%" }}
          />
          <hr />

          <label>No. of members</label>
          <input type="text" id="team_no" onKeyUp={() => this.setCount(this)} />
          <hr />
          <TeamMemberList />
          <hr />
        </center>
      </div>
    );
  }
}

export default AddGroup;
