import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";
import AddGroup from "../addgroupcontroller";
import { withRouter } from "react-router-dom";

class AddExpense extends Component {
  state = {
    current_data: this.props.current_data
  };

  addExpense = () => {
    let expenses = this.state.current_data.expenses;
    let expensesSize = expenses ? Object.keys(expenses).length : 0;
    let expenseName = this.refs.description.value;
    let totalAmount = this.refs.amount.value;
    let { members_count } = this.state.current_data;
    let amountPerHead = (totalAmount / (Number(members_count) + 1)).toFixed(2);
    console.log(amountPerHead);
    let updateCurrentData = this.state.current_data;
    console.log(this.state.current_data);
    let newExpense = {};
    newExpense["expense_" + expensesSize] = expenseName;
    newExpense["youPaid_" + expensesSize] = totalAmount;
    updateCurrentData.expenses.push(newExpense);
    for (var i = 0; i < updateCurrentData.friends_name.length; i++) {
      updateCurrentData.friends_name[i]["owes_" + i] = amountPerHead;
    }
    //console.log(updateCurrentData.friends_name);

    updateCurrentData.owed = totalAmount - amountPerHead;

    this.setState({ current_data: updateCurrentData });
    AddGroup.setAddExpense(this.state.current_data, () =>
      alert("Expense Added!")
    );
    this.props.history.push("/dashboard");
  };

  render() {
    const { history } = this.props;
    return (
      <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add expense
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <h6>
              With{" "}
              <b>
                <i>you</i>
              </b>{" "}
              and all of:{" "}
              <b>
                <i>{this.props.group_name}</i>
              </b>
            </h6>
          </div>
          <div>
            <center>
              <span style={{ marginRight: "20px" }}>
                <i className="fa fa-arrow-down"></i>
              </span>
              <label>
                <i>Enter a description</i>
              </label>
            </center>
            <center>
              <input type="text" placeholder="Description" ref="description" />
            </center>
            <center>
              <input type="text" placeholder="Amount in Rs." ref="amount" />
            </center>
            <center>
              <label>
                <i>Enter amount</i>
              </label>{" "}
              <span style={{ marginLeft: "60px" }}>
                <i className="fa fa-arrow-up"></i>
              </span>
            </center>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={this.addExpense}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

export default withRouter(AddExpense);

