class AddGroup {
  createGroup(group) {
    let anyGroup = localStorage.getItem("Groups");
    if (anyGroup) {
      anyGroup = JSON.parse(anyGroup);
      anyGroup.push(group);
      localStorage.setItem("Groups", JSON.stringify(anyGroup));
    } else {
      anyGroup = [];
      anyGroup.push(group);
      localStorage.setItem("Groups", JSON.stringify(anyGroup));
    }
  }
}

export default new AddGroup();
