import React, { Component } from "react";
import GroupTags from "./groupTags";
import FriendTags from "./friendtags";
import axios from "axios";

class LeftPanel extends Component {
  state = {
    group_data: this.props.group_data
  };
  groupFriends = [];

  componentDidMount() {
    console.log("props...", this.props.group_data);
    for (let i = 0; i < 3; i++) {
      let groupId = new FormData();

      groupId.append("group_id", this.props.group_data[i].id);

      axios({
        method: "post",
        url: "/app/user/group/friends",
        data: groupId,
        headers: { "Content-Type": "multipart/form-data" }
      })
        .then(response => {
          this.groupFriends = this.groupFriends.concat(response.data.friends);
          console.log(
            "----------------------------------------",
            response.data.friends
          );
        })
        .catch(error => console.log(error));
    }
    console.log("groupFriends", this.groupFriends);
  }

  displayGroups() {
    //let groups = JSON.parse(AddGroup.getGroups());
    let groupTags = [];
    if (this.props.group_data) {
      for (var i = 0; i < this.props.group_data.length; i++) {
        groupTags.push(
          <GroupTags
            key={i}
            id={i}
            groupTags={this.props.group_data[i].name}
            group_data={this.props.group_data[i]}
            handleClick={this.props.handleClick}
          />
        );
      }
    }

    return groupTags;
  }

  mapSimilarGrpMembers = () => {
    let belongsToGrps = [];

    for (let j = 0; j < this.groupFriends.length; j++) {
      if (
        !belongsToGrps.some(user => user.name === this.groupFriends[j].name)
      ) {
        belongsToGrps.push({
          name: this.groupFriends[j].name,
          belongsTo: [this.groupFriends[j].groups_id]
        });
      } else {
        for (let k = 0; k < belongsToGrps.length; k++) {
          if (belongsToGrps[k].name === this.groupFriends[j].name) {
            belongsToGrps[k].belongsTo.push(this.groupFriends[j].groups_id);
            break;
          }
        }
      }
    }

    console.log("belongsToGrps", belongsToGrps);
    return belongsToGrps;
  };

  displayGrpFriends = () => {
    let belongsToGrps = this.mapSimilarGrpMembers();
    console.log(belongsToGrps[0]);

    if (belongsToGrps[0]) {
      return (
        <FriendTags
          grpFriends={belongsToGrps}
          handleClick={this.props.displayFrndDetails}
        />
      );
    }
  };

  render() {
    return (
      <div id="leftpanel">
        <div id="view_options">
          <a /*href="www.splitwise.com"*/ id="dashboard_option">Dashboard</a>
          <div className="group_tags">
            <div className="header">
              GROUPS
              <a id="add_group" href="/addgroup">
                + Add
              </a>
            </div>

            {this.displayGroups()}
          </div>

          <div className="group_tags">
            <div className="header">
              FRIENDS
              <a
                style={{ cursor: "pointer" }}
                id="add_group"
                /*href="/addgroup"*/ onClick={() =>
                  this.props.setAddFrndsModalShow(true)
                }
              >
                + Add Friends
              </a>
            </div>
            {this.displayGrpFriends()}
          </div>
        </div>
      </div>
    );
  }
}

export default LeftPanel;
