import React, { Component } from "react";
import GroupTags from "./groupTags";
import FriendTags from "./friendtags";
import axios from "axios";

class LeftPanel extends Component {
  state = {
    groupFriends: {}
  };
  /*
  async componentDidMount() {
    let user = new FormData();
    user.append("user", sessionStorage.getItem("LoggedInUser"));
    let res = await axios({
      method: "post",
      url: "/app/user/group/friends",
      data: user,
      headers: { "Content-Type": "multipart/form-data" }
    });

    console.log("Group Friends: ", res.data.grpFriends);

    let grpFriends = [];
    for (let friend of res.data.grpFriends) {
      grpFriends = grpFriends.concat(friend);
    }
    this.setState({ groupFriends: grpFriends });
  }
*/
  displayGroups() {
    //let groups = JSON.parse(AddGroup.getGroups());
    let groupTags = [];
    if (this.props.group_data) {
      for (var i = 0; i < this.props.group_data.length; i++) {
        groupTags.push(
          <GroupTags
            key={i}
            id={i}
            groupTags={this.props.group_data[i].name}
            group_data={this.props.group_data[i]}
            handleClick={this.props.handleClick}
          />
        );
      }
    }

    return groupTags;
  }

  mapSimilarGrpMembers = () => {
    let belongsToGrps = [];
    if (this.props.group_data.friends) {
      for (let j = 0; j < this.props.group_data.friends.length; j++) {
        if (
          !belongsToGrps.some(
            user => user.name === this.props.group_data.friends[j].name
          )
        ) {
          belongsToGrps.push({
            name: this.props.group_data.friends[j].name,
            belongsTo: [this.props.group_data.friends[j].groups_id]
          });
        } else {
          for (let k = 0; k < belongsToGrps.length; k++) {
            if (
              belongsToGrps[k].name === this.props.group_data.friends[j].name
            ) {
              belongsToGrps[k].belongsTo.push(
                this.props.group_data.friends[j].groups_id
              );
              break;
            }
          }
        }
      }

      console.log("belongsToGrps", belongsToGrps);
      return belongsToGrps;
    }
  };

  displayGrpFriends = () => {
    let belongsToGrps = this.mapSimilarGrpMembers();
    if (belongsToGrps) {
      return (
        <FriendTags
          grpFriends={belongsToGrps}
          handleClick={this.props.displayFrndDetails}
        />
      );
    }
  };

  render() {
    return (
      <div id="leftpanel">
        <div id="view_options">
          <a /*href="www.splitwise.com"*/ id="dashboard_option">Dashboard</a>
          <div className="group_tags">
            <div className="header">
              GROUPS
              <a id="add_group" href="/addgroup">
                + Add
              </a>
            </div>

            {this.displayGroups()}
          </div>

          <div className="group_tags">
            <div className="header">
              FRIENDS
              <a
                style={{ cursor: "pointer" }}
                id="add_group"
                /*href="/addgroup"*/ onClick={() =>
                  this.props.setAddFrndsModalShow(true)
                }
              >
                + Add Friends
              </a>
            </div>
            {this.displayGrpFriends()}
          </div>
        </div>
      </div>
    );
  }
}

export default LeftPanel;
