import React, { Component } from "react";

class AddExpense extends Component {
  state = {};

  toggleModal = e => {
    let modal = this.modalElement;
    if (modal.style.display === "none") modal.style.display = "block";
    else modal.style.display = "none";
  };

  render() {
    return (
      <div
        ref={modal => (this.modalElement = modal)}
        className="modal"
        id="postModal"
        style={{ display: "none" }}
      >
        <div className="popup">
          <h2>Add New Movie</h2>
          <button
            ref={modalClose => (this.modalCloseElement = modalClose)}
            id="closeButton"
            onClick={e => this.toggleModal(e)}
            className="close"
            style={{ height: "5vh" }}
          >
            &times;
          </button>
          <div className="content">
            <input
              type="text"
              placeholder="Movie Name"
              className="inputs"
              name="movieName"
              value={this.state.movieName}
              onChange={e => this.change(e)}
              style={{ height: "4vh" }}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Movie Description"
              className="inputs"
              name="movieDesc"
              value={this.state.movieDesc}
              onChange={e => this.change(e)}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Cast"
              className="inputs"
              name="movieCast"
              value={this.state.movieCast}
              onChange={e => this.change(e)}
              style={{ height: "4vh" }}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Director"
              className="inputs"
              name="movieDirector"
              value={this.state.movieDirector}
              onChange={e => this.change(e)}
              style={{ height: "4vh" }}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Rating (on a scale of 5)"
              className="inputs"
              name="movieRating"
              value={this.state.movieRating}
              onChange={e => this.change(e)}
              style={{ height: "4vh" }}
            ></input>
            <br />
            <br />
            <br />
            <label style={{ color: "black" }}>
              Upload Image ---> &nbsp;&nbsp;
            </label>
            <input
              style={{ width: "60%" }}
              type="file"
              name="movieImage"
              onChange={e => this.imageUpload(e)}
              style={{ color: "black" }}
            ></input>
            <br />
            <br />
            <button onClick={e => this.addMovie(e)} id="modalPostButton">
              Add Movie
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default AddExpense;
