import React, { Component } from "react";

class GroupTags extends Component {
  state = { isClicked: false };

  setBackground = e => {
    let clicked = e.target.id;
    if (this.state.isClicked === clicked) {
      this.setState({ isClicked: true });
    } else {
      this.setState({ isClicked: false });
    }
  };

  render() {
    return (
      <a
        href="www.splitwise.com"
        className={this.state.isClicked === true ? "group_name open" : ""}
        onClick={this.setBackground}
      >
        <i className="fa fa-tag"></i> {this.props.groupTags}
      </a>
    );
  }
}

export default GroupTags;
