import React, { Component } from "react";
import TeamMemberList from "./team_member_list";

class AddGroup extends Component {
  state = {
    members_count: 0,
    friends_name: []
  };

  setCount = e => {
    this.setState({ members_count: this.refs.team_no.value });
  };

  create_fields() {
    let field = "";

    for (let index = 0; index < this.state.members_count; index++) {
      field += <TeamMemberList id={"friend" + index} />;
    }
    return field;
  }

  render() {
    return (
      <div>
        <center>
          <h2>
            <label>My Group shall be called as...</label>
          </h2>
          <input
            type="text"
            name="group_name"
            ref="group_name"
            placeholder="Group Name"
            style={{ width: "50%" }}
          />
          <hr />
          <label>No. of members: </label>
          <input type="text" ref="team_no" />
          <button className="btn btn-info" onClick={this.setCount}>
            Add Friends!
          </button>
          <hr />
          {this.create_fields()}
          <br />
          <br />
          <button className="btn btn-info">Create Group!</button>
          <hr />
        </center>
      </div>
    );
  }
}

export default AddGroup;
