import React, { Component } from "react";
import TeamMemberList from "./team_member_list";
import addGroup from "../addgroupcontroller";

class AddGroup extends Component {
  state = {
    group_name: "",
    members_count: 0,
    created_by: sessionStorage.getItem("LoggedInUser"),
    friends_name: []
  };

  componentDidUpdate() {
    this.storeGroup();
  }

  setCount = e => {
    this.setState({ members_count: this.refs.team_no.value });
  };

  create_fields() {
    let field = [];

    for (let index = 0; index < this.state.members_count; index++) {
      field.push(<TeamMemberList key={index} id={index} />);
    }
    return field;
  }

  handleGroupName = e => {
    this.setState({ group_name: e.target.value });
  };

  setFriends = (friends, callback) => {
    this.setState({ friends_name: friends });
  };

  storeGroup = () => {
    addGroup.createGroup(this.state, () =>
      this.props.history.push("/dashboard")
    );
  };

  createGroup = e => {
    e.preventDefault();
    let friends = [];

    for (let i = 0; i < this.state.members_count; i++) {
      friends.push(document.getElementById(i).value);
    }

    this.setFriends(friends, this.storeGroup);
  };

  render() {
    return (
      <div>
        <form>
          <center>
            <h2>
              <label>My Group shall be called as...</label>
            </h2>
            <input
              type="text"
              name="group_name"
              placeholder="Group Name"
              style={{ width: "50%" }}
              //onChange={this.handleGroupName}
            />
            <hr />
            <label>No. of members: </label>
            <input type="text" ref="team_no" />
            <button
              type="button"
              className="btn btn-info"
              onClick={this.setCount}
            >
              Add Friends!
            </button>
            <hr />
            {this.create_fields()}
            <br />
            <br />
            <button
              type="submit"
              onClick={this.createGroup}
              className="btn btn-info"
            >
              Create Group!
            </button>
            <hr />
          </center>
        </form>
      </div>
    );
  }
}

export default AddGroup;
