import React, { Component } from "react";
import GroupTags from "./groupTags";
import FriendTags from "./friendtags";
import AddGroup from "../addgroupcontroller";

class LeftPanel extends Component {
  state = {
    friends: {}
  };

  displayGroups() {
    //let groups = JSON.parse(AddGroup.getGroups());
    let groupTags = [];
    if (this.props.group_data) {
      for (var i = 0; i < this.props.group_data.length; i++) {
        groupTags.push(
          <GroupTags
            key={i}
            id={i}
            groupTags={this.props.group_data[i].group_name}
            group_data={this.props.group_data[i]}
            handleClick={this.props.handleClick}
          />
        );
      }
    }

    return groupTags;
  }

  mapSimilarGrpMembers = () => {
    let groups = JSON.parse(AddGroup.getGroups());
    let belongsToGrps = [];
    for (let i = 0; i < groups.length; i++) {
      for (let j = 0; j < groups[i].friends_name.length; j++) {
        if (
          !belongsToGrps.some(
            user => user.name === groups[i].friends_name[j]["friend_" + j]
          )
        ) {
          belongsToGrps.push({
            name: groups[i].friends_name["friend_" + i],
            belongsTo: groups[i].group_name
          });
        }
      }
      console.log(belongsToGrps);
    }
  };

  displayGrpFriends = () => {
    this.mapSimilarGrpMembers();

    let grpFriends = [];
    if (this.props.group_data) {
      for (var i = 0; i < this.props.group_data.length; i++) {
        grpFriends.push(
          <FriendTags
            key={i}
            id={i}
            grpFriends={this.props.group_data[i].friends_name}
          />
        );
      }
    }

    return grpFriends;
  };

  render() {
    return (
      <div id="leftpanel">
        <div id="view_options">
          <a /*href="www.splitwise.com"*/ id="dashboard_option">Dashboard</a>
          <div className="group_tags">
            <div className="header">
              GROUPS
              <a id="add_group" href="/addgroup">
                + Add
              </a>
            </div>

            {this.displayGroups()}
          </div>

          <div className="group_tags">
            <div className="header">
              FRIENDS
              <a
                style={{ cursor: "pointer" }}
                id="add_group"
                /*href="/addgroup"*/ onClick={() =>
                  this.props.setAddFrndsModalShow(true)
                }
              >
                + Add Friends
              </a>
            </div>
            {this.displayGrpFriends()}
          </div>
        </div>
      </div>
    );
  }
}

export default LeftPanel;
