import axios from "axios";

class Auth {
  /*constructor() {
    this.authenticated = false;
  }*/

  hasAnyGroup = async () => {
    let currentUser = new FormData();
    currentUser.append(
      "user",
      "abc" /*sessionStorage.getItem("LoggedInUser")*/
    );
    let group = await axios({
      method: "post",
      url: "/app/get/group",
      data: currentUser,
      headers: { "Content-Type": "multipart/form-data" }
    });
    console.log("auth: ", group);
  };

  login(user, callback) {
    //this.authenticated = true;
    sessionStorage.setItem("LoggedInUser", user);
    this.hasAnyGroup();
    callback();
  }

  logout() {
    //this.authenticated = false;
    sessionStorage.removeItem("LoggedInUser");
    sessionStorage.removeItem("current_grp_switch");
    //callback();
  }

  isAuthenticated() {
    let authenticated = sessionStorage.getItem("LoggedInUser");
    return authenticated;
  }

  getCurrentGrpSwitch() {
    return sessionStorage.getItem("current_grp_switch");
  }

  setCurrentGrpSwitch(groupName) {
    sessionStorage.setItem("current_grp_switch", groupName);
  }
}

export default new Auth();
