import React, { Component } from "react";
import AddGroup from "../addgroupcontroller";
import GroupTags from "./groupTags";

class LeftPanel extends Component {
  state = { groups: JSON.parse(localStorage.getItem("Groups")) };

  displayGroups() {
    let groups = JSON.parse(AddGroup.getGroups());
    let groupTags = [];

    for (var i = 0; i < groups.length; i++) {
      groupTags.push(
        <GroupTags key={i} id={i} groupTags={groups[i].group_name} />
      );
    }

    return groupTags;
  }

  render() {
    return (
      <div id="leftpanel">
        <div id="view_options">
          <a href="www.splitwise.com" id="dashboard_option">
            Dashboard
          </a>
          <div className="group_tags">
            <div className="header">
              GROUPS
              <a id="add_group" href="www.spitwise.com">
                + Add
              </a>
            </div>

            {this.displayGroups()}
          </div>

          <div className="group_tags">
            <div className="header">FRIENDS</div>
            <a href="www.splitwise.com" className="group_name">
              <i className="fa fa-user"></i> Karthick
            </a>
            <a href="www.splitwise.com" className="group_name">
              <i className="fa fa-user"></i> Vishnu
            </a>
            <a href="www.splitwise.com" className="group_name">
              <i className="fa fa-user"></i> Dhayal
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default LeftPanel;
