class AddGroup {
  createGroup(group, callback) {
    let anyGroup = localStorage.getItem("Groups");
    if (anyGroup) {
      anyGroup.push(group);
      localStorage.setItem("Groups", JSON.stringify(anyGroup));
    } else {
      anyGroup = [];
      anyGroup.push(group);
      localStorage.setItem("Groups", JSON.stringify(anyGroup));
    }
    callback();
  }
}

export default new AddGroup();
